import React from 'react';

function DrowDiamond() {

  const drowDiamond = () => {
    let text = '';

    //최대 15개의 별까지
    //가운데에 별 노출
    // 1,3,5,7,9,11,13,15,13,11,9,7,5,3,1
    // 여백은 nbsp?
    // 포문으로 15까지 움직인다. 15넘어가면 엔터
    // 15안에서 별이냐 스페이스냐를 리턴
    for(let j = 0; j < 15; j++){
      for(let i = 0; i < 15; i++){
        text += '-';
        if(i === 14){
          text += '\n';
        }
      }
    }
    return text;
  }

  return (
    <pre>{drowDiamond()}</pre>
    );
}

export default DrowDiamond;
